import { useState } from "react";
import axios from "axios";
import "./Banner.css";

export default function StickyBanner() {
    const [userEmail, setUserEmail] = useState("");
    const [emailSent, setEmailSent] = useState(false);

    const handleChange = (e) => {
      setUserEmail(e.target.value);
    };

    async function submitEmail() {
      const data = { email: userEmail };
      const url =
        "https://accountablemuj3pl9f-registerbloglead.functions.fnc.fr-par.scw.cloud";
      await axios.post(url, data).then((response) => {
        if (response.data.message === "ok") {
          setEmailSent(true);
        }
      });
    }

    const handleSubmit = (e) => {
      e.preventDefault();
      submitEmail();
    };
  return (
      <div className="StickyBanner">
        <div className="StickyBanner__content">
          <form className="StickyBanner__form" onSubmit={handleSubmit}>
            <input
              className="StickyBanner__input"
              type="email"
              placeholder="Your email address"
              onChange={handleChange}
              required
            />
            <button type="submit" className="StickyBanner__button"></button>
          </form>
        </div>
      </div>
  );
}
